mapper.update:
UPDATE CALL_HISTORY
SET call_Type = #{call_Type},
    call_Time = #{call_Time},
    call_Duration = #{call_Duration}
WHERE call_Seq = #{call_Seq}
     AND c_Phone = #{c_Phone} 
     AND u_Id = #{u_Id}

mapper.delete:
        DELETE FROM CALL_HISTORY 
        WHERE call_Seq = #{call_Seq}
        	AND call_Id = #{call_Id}
	        AND c_Phone = #{c_Phone} 
	        AND u_Id = #{u_Id}
