mapper.update:
UPDATE SMS_HISTORY
SET sms_Type = #{sms_Type},
    sms_Contents = #{sms_Contents},
    sms_Time = #{sms_Time}
WHERE sms_Seq = #{sms_Seq}
     AND c_Phone = #{c_Phone} 
     AND u_Id = #{u_Id}

mapper.delete:
        DELETE FROM SMS_HISTORY 
        WHERE sms_Seq = #{sms_Seq}
        	AND sms_Id = #{sms_Id}
	        AND c_Phone = #{c_Phone} 
	        AND u_Id = #{u_Id}
